mapper.update:
UPDATE t_saved_filter
SET name = #{name},
    filter_json = #{filterJson},
    updated_at = CURRENT_TIMESTAMP
WHERE id = #{id}
  AND user_id = #{userId}

mapper.select:
SELECT id, user_id, family_id, name, filter_json, created_at, updated_at
FROM t_saved_filter
WHERE id = #{id}
  AND user_id = #{userId}
LIMIT 1

SELECT id, user_id, family_id, name, filter_json, created_at, updated_at
FROM t_saved_filter
WHERE user_id = #{userId}
  AND family_id = #{familyId}
ORDER BY updated_at DESC

mapper.delete:
DELETE FROM t_saved_filter
WHERE id = #{id}
  AND user_id = #{userId}

mapper.select:
SELECT id, user_id, family_id, name, filter_json, created_at, updated_at
FROM t_saved_filter
WHERE user_id = #{userId}
  AND family_id = #{familyId}
  AND name = #{name}
LIMIT 1
